import React from 'react';

const StatsSection = ({ stats }) => {
  return (
    <section className="bg-est-blue text-white py-12 md:py-16">
      <div className="container mx-auto px-4">
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 md:gap-8 text-center">
          <div className="p-2 md:p-4">
            <div className="text-2xl md:text-4xl font-bold mb-1 md:mb-2">
              {stats.placementRate || '95%'}
            </div>
            <div className="text-sm md:text-xl">Taux d'insertion</div>
          </div>
          <div className="p-2 md:p-4">
            <div className="text-2xl md:text-4xl font-bold mb-1 md:mb-2">
              {stats.partners || '15+'}
            </div>
            <div className="text-sm md:text-xl">Partenaires industriels</div>
          </div>
          <div className="p-2 md:p-4">
            <div className="text-2xl md:text-4xl font-bold mb-1 md:mb-2">
              {stats.students || '1200'}
            </div>
            <div className="text-sm md:text-xl">Ã‰tudiants</div>
          </div>
          <div className="p-2 md:p-4">
            <div className="text-2xl md:text-4xl font-bold mb-1 md:mb-2">
              {stats.teachers || '50+'}
            </div>
            <div className="text-sm md:text-xl">Enseignants experts</div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default StatsSection;
