import { useState, useEffect, useContext, createContext } from 'react';
import { auth } from '../services/auth';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(() => {
    // Check localStorage for existing user data
    const storedUser = localStorage.getItem('estSaleUser');
    return storedUser ? JSON.parse(storedUser) : null;
  });
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const checkAuth = async () => {
      try {
        if (user) {
          // Verify the existing user session if needed
          const userData = await auth.checkAuth();
          if (!userData) {
            localStorage.removeItem('estSaleUser');
            setUser(null);
          }
        }
      } catch (error) {
        localStorage.removeItem('estSaleUser');
        setUser(null);
      } finally {
        setLoading(false);
      }
    };
    checkAuth();
  }, [user]);

  const login = async (credentials) => {
    try {
      const userData = await auth.login(credentials);
      localStorage.setItem('estSaleUser', JSON.stringify(userData));
      setUser(userData);
      // Redirect based on role
      if (userData.role === 'etudiant') {
        navigate('/profil/etudiant');
      } else {
        navigate('/profil/enseignant');
      }
      return userData;
    } catch (error) {
      throw error;
    }
  };

  const logout = async () => {
    await auth.logout();
    localStorage.removeItem('estSaleUser');
    setUser(null);
    navigate('/');
  };

  return (
    <AuthContext.Provider value={{ user, loading, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
