import { useState, useEffect, useContext, createContext } from 'react';
import { auth } from '../services/auth';
import { useNavigate, useLocation } from 'react-router-dom';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const checkAuth = async () => {
      const token = localStorage.getItem('authToken');
      if (token) {
        try {
          const userData = await auth.getUserMetadata(token);
          setUser({ ...userData, token });
        } catch (error) {
          localStorage.removeItem('authToken');
          setUser(null);
        }
      }
      setLoading(false);
    };

    // Handle OAuth callback
    const params = new URLSearchParams(location.search);
    const code = params.get('code');
    if (code) {
      const handleAuth = async () => {
        try {
          const { access_token } = await auth.handleCallback(code);
          localStorage.setItem('authToken', access_token);
          const userData = await auth.getUserMetadata(access_token);
          setUser({ ...userData, token: access_token });
          navigate('/');
        } catch (error) {
          console.error('Authentication failed:', error);
        }
      };
      handleAuth();
    } else {
      checkAuth();
    }
  }, [location, navigate]);

  const login = async () => {
    await auth.initiateLogin();
  };

  const logout = async () => {
    await auth.logout();
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, loading, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
