// src/components/Navbar.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useKeycloak } from '@react-keycloak/web';

const Navbar = () => {
  const { keycloak } = useKeycloak();

  return (
    <header className="bg-est-blue text-white shadow-md sticky top-0 z-50">
      <div className="container mx-auto px-4 py-4">
        <div className="flex justify-between items-center">
          <div className="flex items-center">
            <img src="/logo.png" alt="Logo EST Salé" className="h-10 mr-3" />
            <Link to="/" className="text-xl md:text-2xl font-bold">EST Salé</Link>
          </div>
          
          <nav className="hidden md:flex space-x-6">
            <Link to="/" className="hover:underline">Accueil</Link>
            <Link to="/formations" className="hover:underline">Formations</Link>
            {keycloak.authenticated && (
              <Link to="/admin" className="hover:underline">Admin</Link>
            )}
            {keycloak.authenticated ? (
              <button 
                onClick={() => keycloak.logout()}
                className="bg-est-yellow text-black px-4 py-1 rounded hover:bg-yellow-600"
              >
                Déconnexion
              </button>
            ) : (
              <button 
                onClick={() => keycloak.login()}
                className="bg-est-yellow text-black px-4 py-1 rounded hover:bg-yellow-600"
              >
                Connexion
              </button>
            )}
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
