import React from 'react';
import { Link } from 'react-router-dom';
import ProgramCard from '../cards/ProgramCard';

const ProgramsSection = ({ formations }) => {
  return (
    <section className="bg-gray-50 py-12 md:py-16">
      <div className="container mx-auto px-4">
        <h2 className="text-2xl md:text-3xl font-bold text-est-blue text-center mb-4">
          Nos Formations
        </h2>
        <p className="text-lg md:text-xl text-gray-600 text-center mb-8 md:mb-12 max-w-3xl mx-auto">
          Des parcours professionnalisants adaptés aux besoins du marché
        </p>
        
        <div className="grid sm:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-6 lg:gap-8">
          {formations.slice(0, 3).map(formation => (
            <ProgramCard key={formation.id} formation={formation} />
          ))}
        </div>
        
        <div className="text-center mt-8 md:mt-12">
          <Link 
            to="/formations" 
            className="inline-block bg-est-blue text-white py-2 px-6 md:py-3 md:px-8 rounded-full hover:bg-blue-900 font-medium transition text-sm md:text-base"
          >
            Voir toutes nos formations
          </Link>
        </div>
      </div>
    </section>
  );
};

export default ProgramsSection;
