// src/contexts/AuthContext.js
import { createContext, useContext, useEffect, useState } from 'react';
import keycloak from '../keycloak';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [initialized, setInitialized] = useState(false);

  useEffect(() => {
    const updateUser = () => {
      if (keycloak.authenticated) {
        setUser({
          name: keycloak.tokenParsed?.name,
          email: keycloak.tokenParsed?.email,
          roles: keycloak.tokenParsed?.realm_access?.roles || []
        });
        setIsAdmin(keycloak.hasRealmRole('admin'));
      } else {
        setUser(null);
        setIsAdmin(false);
      }
      setInitialized(true);
    };

    // Set up event listeners
    keycloak.onAuthSuccess = updateUser;
    keycloak.onAuthRefreshSuccess = updateUser;
    keycloak.onAuthLogout = () => {
      setUser(null);
      setIsAdmin(false);
    };

    // Initial check
    updateUser();

    return () => {
      // Cleanup event listeners
      keycloak.onAuthSuccess = null;
      keycloak.onAuthRefreshSuccess = null;
      keycloak.onAuthLogout = null;
    };
  }, []);

  const login = () => keycloak.login();
  const logout = () => keycloak.logout();

  return (
    <AuthContext.Provider value={{ user, isAdmin, login, logout, initialized }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
