import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';

const Navbar = () => {
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const { user, isAdmin, login, logout } = useAuth();
  const navigate = useNavigate();

  return (
    <header className="bg-est-blue text-white shadow-md sticky top-0 z-50">
      <div className="container mx-auto px-4 py-4">
        <div className="flex justify-between items-center">
          <div className="flex items-center">
            <img src="/logo.png" alt="Logo EST Salé" className="h-10 mr-3" />
            <Link to="/" className="text-xl md:text-2xl font-bold">EST Salé</Link>
          </div>
          
          <nav className="hidden md:flex space-x-1 lg:space-x-6">
            <Link to="/" className="px-3 py-1 rounded hover:bg-est-light-blue hover:bg-opacity-30 transition">Accueil</Link>
            <Link to="/formations" className="px-3 py-1 rounded hover:bg-est-light-blue hover:bg-opacity-30 transition">Formations</Link>
            <Link to="/contact" className="px-3 py-1 rounded hover:bg-est-light-blue hover:bg-opacity-30 transition">Contact</Link>
            
            {user ? (
              <>
                {isAdmin && (
                  <Link to="/admin" className="px-3 py-1 rounded hover:bg-est-light-blue hover:bg-opacity-30 transition">Admin</Link>
                )}
                <button onClick={logout} className="px-3 py-1 bg-est-yellow text-black rounded hover:bg-yellow-600 transition">Déconnexion</button>
              </>
            ) : (
              <button onClick={login} className="px-3 py-1 bg-est-yellow text-black rounded hover:bg-yellow-600 transition">Connexion</button>
            )}
          </nav>
          
          <button 
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            className="md:hidden text-white focus:outline-none"
          >
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 6h16M4 12h16M4 18h16"></path>
            </svg>
          </button>
        </div>
        
        <div className={`md:hidden bg-est-blue transition-all duration-300 ${mobileMenuOpen ? 'max-h-screen py-3' : 'max-h-0 overflow-hidden'}`}>
          <div className="flex flex-col space-y-2 px-4">
            <Link to="/" className="px-3 py-2 rounded hover:bg-est-light-blue hover:bg-opacity-30 transition">Accueil</Link>
            <Link to="/formations" className="px-3 py-2 rounded hover:bg-est-light-blue hover:bg-opacity-30 transition">Formations</Link>
            <Link to="/contact" className="px-3 py-2 rounded hover:bg-est-light-blue hover:bg-opacity-30 transition">Contact</Link>
            
            {user ? (
              <>
                {isAdmin && (
                  <Link to="/admin" className="px-3 py-2 rounded hover:bg-est-light-blue hover:bg-opacity-30 transition">Admin</Link>
                )}
                <button onClick={logout} className="px-3 py-2 bg-est-yellow text-black rounded hover:bg-yellow-600 transition text-left">Déconnexion</button>
              </>
            ) : (
              <button onClick={login} className="px-3 py-2 bg-est-yellow text-black rounded hover:bg-yellow-600 transition text-left">Connexion</button>
            )}
          </div>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
