import React from 'react';
import { Link } from 'react-router-dom';

const EventCard = ({ event }) => {
  return (
    <div className="bg-white rounded-xl shadow-md overflow-hidden">
      <div className="bg-est-blue text-white p-3 md:p-4 text-center">
        <div className="text-xl md:text-2xl font-bold">{event.day}</div>
        <div className="text-xs md:text-sm">{event.month}</div>
      </div>
      <div className="p-4 md:p-6">
        <h3 className="text-lg md:text-xl font-semibold text-est-blue mb-2 md:mb-2">{event.title}</h3>
        <p className="text-gray-600 text-sm md:text-base mb-3 md:mb-4">{event.description}</p>
        <div className="flex items-center text-gray-500 text-xs md:text-sm mb-3 md:mb-4">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-3 w-3 md:h-4 md:w-4 mr-1 md:mr-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
          </svg>
          {event.location}
        </div>
        <Link 
          to={`/evenements/${event.id}`} 
          className="text-est-blue font-medium hover:underline text-sm md:text-base"
        >
          {event.actionText || "Plus d'infos"}
        </Link>
      </div>
    </div>
  );
};

export default EventCard;
