// src/App.js
import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import { AuthProvider } from './contexts/AuthContext';
import HomePage from './pages/HomePage';
import EtudiantProfile from './pages/EtudiantProfile';
import EnseignantProfile from './pages/EnseignantProfile';
import PrivateRoute from './components/common/PrivateRoute';
import Navbar from './components/common/Navbar';
import Footer from './components/common/Footer';
import { initKeycloak, keycloak } from './keycloak';

function App() {
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    initKeycloak(() => {
      setAuthenticated(true);
    });
  }, []);

  const handleLogin = () => {
    keycloak.login();
  };

  if (!authenticated) {
    return (
      <div className="error-container">
        <h2>Authentication Required</h2>
        <button onClick={handleLogin}>Login</button>
        <p>If you see this message repeatedly, check:</p>
        <ul>
          <li>Keycloak server is running</li>
          <li>Correct realm and client ID</li>
          <li>Network connectivity</li>
        </ul>
      </div>
    );
  }

  return (
    <AuthProvider>
      <div className="font-sans bg-gray-50 min-h-screen flex flex-col">
        <Navbar />
        <main className="flex-grow">
          <Routes>
            <Route path="/" element={<HomePage />} />
            
            <Route element={<PrivateRoute role="etudiant" />}>
              <Route path="/profil/etudiant" element={<EtudiantProfile />} />
            </Route>
            
            <Route element={<PrivateRoute role="enseignant" />}>
              <Route path="/profil/enseignant" element={<EnseignantProfile />} />
            </Route>
          </Routes>
        </main>
        <Footer />
      </div>
    </AuthProvider>
  );
}

export default App;
