import React from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { ReactKeycloakProvider } from '@react-keycloak/web';
import { keycloak, initOptions } from './keycloak';
import Navbar from './components/common/Navbar';
import Footer from './components/common/Footer';
import HomePage from './pages/HomePage';
import FormationsPage from './pages/FormationsPage';
import NewsPage from './pages/NewsPage';
import EventsPage from './pages/EventsPage';
import LoginPage from './pages/LoginPage';
import ContactPage from './pages/ContactPage';
import PrivateRoute from './components/common/PrivateRoute';
import LoadingSpinner from './components/common/LoadingSpinner';
import './App.css';

const App = () => {
  const handleOnEvent = (event, error) => {
    if (event === 'onAuthSuccess') {
      if (keycloak.authenticated) {
        console.log('Authenticated successfully');
      }
    }
    if (event === 'onAuthError') {
      console.error('Authentication Error:', error);
    }
  };

  const loadingComponent = (
    <div className="flex justify-center items-center h-screen">
      <LoadingSpinner size="large" />
      <p className="ml-4">Initializing authentication...</p>
    </div>
  );

  return (
    <ReactKeycloakProvider
      authClient={keycloak}
      initOptions={initOptions}
      onEvent={handleOnEvent}
      LoadingComponent={loadingComponent}
    >
      <Router>
        <div className="flex flex-col min-h-screen">
          <Navbar />
          <main className="flex-grow">
            <Switch>
              <Route exact path="/" component={HomePage} />
              <Route path="/formations" component={FormationsPage} />
              <Route path="/actualites" component={NewsPage} />
              <Route path="/evenements" component={EventsPage} />
              <Route path="/contact" component={ContactPage} />
              <Route path="/login" component={LoginPage} />
              <PrivateRoute path="/admin" component={AdminPage} roles={['admin']} />
            </Switch>
          </main>
          <Footer />
        </div>
      </Router>
    </ReactKeycloakProvider>
  );
};

export default App;
