// src/contexts/AuthContext.js
import { createContext, useContext, useEffect, useState } from 'react';
import Keycloak from 'keycloak-js';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [keycloak, setKeycloak] = useState(null);
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    const keycloakInstance = new Keycloak({
      url: process.env.REACT_APP_KEYCLOAK_URL || 'http://localhost:8080',
      realm: process.env.REACT_APP_KEYCLOAK_REALM || 'mon-realm',
      clientId: process.env.REACT_APP_KEYCLOAK_CLIENT_ID || 'react-client'
    });

    keycloakInstance.init({ onLoad: 'check-sso' })  // Changed from 'login-required'
      .then(authenticated => {
        setKeycloak(keycloakInstance);
        setAuthenticated(authenticated);
      })
      .catch(console.error);

    return () => {
      if (keycloakInstance) {
        keycloakInstance.logout();
      }
    };
  }, []);

  return (
    <AuthContext.Provider value={{ keycloak, authenticated }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
