import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import keycloak from '../../keycloak';




const Navbar = () => {
  const [authenticated, setAuthenticated] = useState(false);
  const [profile, setProfile] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const updateAuthStatus = () => {
      setAuthenticated(keycloak.authenticated);
      if (keycloak.authenticated) {
        loadProfile();
      }
    };

    keycloak.onAuthSuccess = updateAuthStatus;
    keycloak.onAuthRefreshSuccess = updateAuthStatus;
    keycloak.onAuthLogout = () => {
      setAuthenticated(false);
      setProfile(null);
    };

    // Initialize Keycloak
    keycloak.init({ onLoad: 'check-sso' }).then(updateAuthStatus);
  }, []);

  const loadProfile = async () => {
    try {
      const response = await fetch('/user/profile', {
        headers: {
          'Authorization': `Bearer ${keycloak.token}`
        }
      });
      if (response.ok) {
        setProfile(await response.json());
      }
    } catch (error) {
      console.error('Failed to load profile', error);
    }
  };

  const handleLogin = () => {
    keycloak.login();
  };

  const handleLogout = () => {
    keycloak.logout();
    navigate('/');
  };

  return (
    <header className="bg-est-blue text-white shadow-md sticky top-0 z-50">
      <div className="container mx-auto px-4 py-4">
        <div className="flex justify-between items-center">
          <div className="flex items-center">
            <img src="/assets/images/logo.png" alt="Logo EST Salé" className="h-10 mr-3" />
            <Link to="/" className="text-xl md:text-2xl font-bold">EST Salé</Link>
          </div>
          
          <nav className="hidden md:flex space-x-1 lg:space-x-6 items-center">
            <Link to="/" className="px-3 py-1 rounded hover:bg-est-light-blue hover:bg-opacity-30 transition">Accueil</Link>
            <Link to="/formations" className="px-3 py-1 rounded hover:bg-est-light-blue hover:bg-opacity-30 transition">Formations</Link>
            <Link to="/admission" className="px-3 py-1 rounded hover:bg-est-light-blue hover:bg-opacity-30 transition">Admission</Link>
            <Link to="/recherche" className="px-3 py-1 rounded hover:bg-est-light-blue hover:bg-opacity-30 transition">Recherche</Link>
            
            {authenticated ? (
              <div className="relative group">
                <button className="flex items-center space-x-2 px-3 py-1 rounded hover:bg-est-light-blue hover:bg-opacity-30 transition">
                  <span>{profile?.first_name || 'Profile'}</span>
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clipRule="evenodd" />
                  </svg>
                </button>
                <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg py-1 z-50 hidden group-hover:block">
                  <Link 
                    to="/profile" 
                    className="block px-4 py-2 text-gray-800 hover:bg-gray-100"
                  >
                    Profil
                  </Link>
                  <button
                    onClick={handleLogout}
                    className="block w-full text-left px-4 py-2 text-gray-800 hover:bg-gray-100"
                  >
                    Déconnexion
                  </button>
                </div>
              </div>
            ) : (
              <button 
                onClick={handleLogin}
                className="px-3 py-1 bg-est-yellow text-black rounded hover:bg-yellow-600 transition"
              >
                Connexion
              </button>
            )}
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
