import React, { useEffect, useState } from 'react';
import HeroSection from '../components/sections/HeroSection';
import QuickLinks from '../components/sections/QuickLinks';
import NewsSection from '../components/sections/NewsSection';
import ProgramsSection from '../components/sections/ProgramsSection';
import StatsSection from '../components/sections/StatsSection';
import TestimonialsSection from '../components/sections/TestimonialsSection';
import EventsSection from '../components/sections/EventsSection';
import ContactSection from '../components/sections/ContactSection';
import NewsletterSection from '../components/sections/NewsletterSection';
import MapSection from '../components/sections/MapSection';
import { fetchNews, fetchEvents, fetchTestimonials, fetchStats, fetchFormations } from '../services/api';

const HomePage = () => {
  const [data, setData] = useState({
    news: [],
    events: [],
    testimonials: [],
    stats: {},
    formations: [],
    loading: true,
    error: null
  });

  useEffect(() => {
    const loadData = async () => {
      try {
        const [news, events, testimonials, stats, formations] = await Promise.all([
          fetchNews(),
          fetchEvents(),
          fetchTestimonials(),
          fetchStats(),
          fetchFormations()
        ]);
        
        setData({
          news,
          events,
          testimonials,
          stats,
          formations,
          loading: false,
          error: null
        });
      } catch (error) {
        setData(prev => ({
          ...prev,
          loading: false,
          error: error.message
        }));
      }
    };

    loadData();
  }, []);

  if (data.loading) return <div className="text-center py-12">Chargement en cours...</div>;
  if (data.error) return <div className="text-center py-12 text-red-500">Erreur: {data.error}</div>;

  return (
    <div className="bg-gray-50">
      <HeroSection />
      <QuickLinks />
      <NewsSection news={data.news} />
      <ProgramsSection formations={data.formations} />
      <StatsSection stats={data.stats} />
      <TestimonialsSection testimonials={data.testimonials} />
      <EventsSection events={data.events} />
      <ContactSection />
      <NewsletterSection />
      <MapSection />
    </div>
  );
};

export default HomePage;
