import React from 'react';
import { Link } from 'react-router-dom';

const ProgramCard = ({ formation }) => {
  const getIcon = (type) => {
    switch(type) {
      case 'software':
        return (
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 20l4-16m4 4l4 4-4 4M6 16l-4-4 4-4" />
        );
      case 'network':
        return (
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 3v2m6-2v2M9 19v2m6-2v2M5 9H3m2 6H3m18-6h-2m2 6h-2M7 19h10a2 2 0 002-2V7a2 2 0 00-2-2H7a2 2 0 00-2 2v10a2 2 0 002 2zM9 9h6v6H9V9z" />
        );
      case 'mobile':
        return (
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 18h.01M8 21h8a2 2 0 002-2V5a2 2 0 00-2-2H8a2 2 0 00-2 2v14a2 2 0 002 2z" />
        );
      default:
        return (
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
        );
    }
  };

  return (
    <div className="bg-white rounded-xl shadow-md overflow-hidden transition hover:shadow-lg">
      <div className="p-4 md:p-6">
        <div className="bg-est-blue text-white rounded-full w-10 h-10 md:w-12 md:h-12 flex items-center justify-center mb-3 md:mb-4">
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 md:h-6 md:w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
            {getIcon(formation.type)}
          </svg>
        </div>
        <h3 className="text-lg md:text-xl font-semibold text-est-blue mb-2 md:mb-3">{formation.title}</h3>
        <p className="text-gray-600 text-sm md:text-base mb-3 md:mb-4">{formation.description}</p>
        <Link 
          to={`/formations/${formation.id}`} 
          className="text-est-blue font-medium hover:underline text-sm md:text-base"
        >
          En savoir plus
        </Link>
      </div>
    </div>
  );
};

export default ProgramCard;
